commits.content:
body{    background-image: url(imagens/bright_squares.png) !important;
    background-repeat: repeat !important;
    background-color: #ffffff;
    
}
.navbar{
    background-color: #fff;
    height: 80px;
    margin: 20px;
    border-radius: 16px;
    padding: 0.5rem;
}
.navbar-brand{
    font-weight: 500;
    color: #009970;
    font-size: 24px;
}

.login-button {
  background-color: #009970;
  color: #fff;
  font-size: 14px;
  padding: 8px 20px;
  border-radius: 50px;
  text-decoration: none;
  transition: 0.3s background-color;
}

.login-button:hover {
  background-color: #00b383;
}

.navbar-togger {
  border: none;
  font-size: 1.25rem;
}

.navbar-togger:focus,
.btn-close:focus {
  box-shadow: none;
  outline: none;
}

.nav-link {
  color: #666777;
  font-weight: 500;
  position: relative;
}

.nav-link:hover,
.nav-link.active {
  color: #000;
}

@media (min-width: 991px) {
  .nav-link::before {
    content: "";
    position: absolute;
    bottom: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 0;
    height: 2px;
    background-color: #009970;
    visibility: hidden;
    transition: 0.3s ease-in-out;
  }

  .nav-link:hover::before,
  .nav-link:active::before {
    width: 100%;
    visibility: visible;
  }
}

.c-item {
  height: 480px;
  margin-top: 100px; 
  
}

.c-img {
  height: 100%;
  object-fit: cover;
  border-radius: 50px;
  padding: 8px 20px;
}
.footer-custom {
  background-color: rgb(234, 105, 0);
  color: white;
}

.footer-custom a {
  color: white;
  text-decoration: none; /* remove sublinhado */
}

.footer-custom a:hover {
  color: #f0f0f0; /* fica um pouco mais claro ao passar o mouse */
}
